import { NextPage } from 'next'
const ipify = require('ipify')

const App: NextPage<any> = ({ ip }) => {
  ;(async () => {
    console.log(await ipify({ useIPv6: false }))
    //=> '82.142.31.236'
  })()
  return null
}

App.getInitialProps = async () => {
  const ip = await ipify({ useIPv6: false })
  return { ip }
}

export default App
