import { NextApiRequest, NextApiResponse } from 'next'
import fetch from 'isomorphic-unfetch'

const apiKey = 'e4bd1590-a940-47b3-b705-91ea25395c14'

export default async (req: NextApiRequest, res: NextApiResponse) => {
  try {
    const ipString = req.query.ip as string
    const ip = parseInt(ipString)
    console.log(req.query)
    if (!ip || (ip >= 0 && ip <= 255)) throw new Error('Invalid ip address')
    const response = await fetch(`https://signals.api.auth0.com/v2.0/ip/${ip}`, {
      method: 'get',
      headers: { 'X-Auth-Token': apiKey },
    })
    const data = await response.json()
    console.log(data)
    return res.status(200).json(data)
  } catch (error) {
    console.log(error)
    return res.status(401).json({ message: error.message })
  }
}
