import { NextPage, NextPageContext, NextApiRequest, NextApiResponse } from 'next'

const InfoAPI: NextPage = () => null
interface PageContext extends NextPageContext {
  req: NextApiRequest
  res: NextApiResponse
}

InfoAPI.getInitialProps = async ({ req, res }: PageContext) => {
  const ip = req.headers['x-forwarded-for'] || req.connection.remoteAddress
  res.setHeader('Content-Type', 'application/json')
  res.write(JSON.stringify({ ip }))
  res.end()
}

export default InfoAPI
